import React from "react";
import { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { userInfo } from "../../../utils/auth";
import { loadJobApplicantsByJobId } from "../../../api/apiClient";
import Layout from "../../Layout";
import { ListGroup, ListGroupItem, Button } from "reactstrap";

const LoadJobApplicantsByJobId = () => {
    const { job_id } = useParams();
    const [resData, setResData] = useState(null);
    useEffect(() => {
        const { token } = userInfo();
        loadJobApplicantsByJobId(token, job_id)
            .then(res => {
                setResData(res.data);
            })
            .catch(err => { })
    }, []);
    return (
        <div>
            <Layout title="Job Applicants" className="container">
                <h5>Select,Reject, if selected go to a new Table</h5>
                <ListGroup>
                    {resData && resData.map(data =>
                    (<ListGroupItem>
                        {data.email}
                        <Button
                            color="success"
                            size="sm"
                        >
                            Select
                        </Button>
                        <Button
                            color="danger"
                            size="sm"
                        >
                            Reject
                        </Button>
                    </ListGroupItem>)
                    )}
                </ListGroup>
            </Layout>
        </div>
    )
}

export default LoadJobApplicantsByJobId;
