import React from "react";
import { Component } from "react";
import TagsInput from "./TagsInput";
import Layout from "../../Layout";

class JobAdvertise extends Component {
    state = {
        tags: [],
    }

    handleKeyDown = e => {
        if (e.key !== 'Enter') return;
        const value = e.target.value;
        if (!value.trim()) return;
        this.setState({
            tags: [...this.state.tags, value]
        })
        e.target.value = '';
    }

    removeTag = index => {
        this.setState({
            tags: this.state.tags.filter((el, i) => i !== index),
        })
    }

    render() {
        return (
            <Layout title="Create New Job" className="container col-md-8 offset-md-2">
                <div>
                    <h2>Enter job skill-requirement tags:</h2>
                    <TagsInput tags={this.state.tags} handleKeyDown={e => this.handleKeyDown(e)} removeTag={this.removeTag} />
                </div>
            </Layout>
        )
    }
}

export default JobAdvertise;
