import React from 'react';
import { Link } from 'react-router-dom';
import { API } from '../../../utils/config';

const Card = ({ job }) => {
    const titleStyle = {
        display: "block",
        textOverflow: "ellipsis",
        wordWrap: "break-word",
        overflow: "hidden",
        maxHeight: "2em",
        lineHeight: "1em"
    }
    return (
        <div className="col-md-4 col-sm-4 col-xs-12">
            <div className="card">
                <div className="card-body">
                    <div style={{ minHeight: "3em" }}>
                        <p style={titleStyle}>{job.title}</p>
                    </div>
                    <p>Requires Skills: {job.required_skills}</p>
                    <p>Application Starting Time: {job.application_start_time}</p>
                    <p>Application Ending Time: {job.application_ending_date}</p>
                    <p>Possible Job Starting Time: {job.starting_date}</p>
                    <Link to={`${API}/`}>
                        <button className="btn btn-outline-warning btn-sm">Apply Now</button>
                    </Link>
                </div>
            </div>
        </div>
    )
}

export default Card;
