import React from "react";
import { Component } from "react";
import { loadAllJobAdvertise } from "../../api/apiWorker";
import { userInfo } from "../../utils/auth";

class LoadJobs extends Component {
    state = {
        jobs: null,
        role: null
    }
    componentDidMount() {
        const { token, role } = userInfo();
        loadAllJobAdvertise(token)
            .then(res => {
                console.log(res.data);
                this.setState({
                    jobs: res.data,
                    role: role
                })
                console.log(this.state.jobs);
            })
            .catch(err => {
                console.log(err);
            })
    }
    render() {
        return (
            <div>
                {this.state.role === 'worker'
                    && JSON.stringify(this.state.jobs, null, 4)}
            </div>
        )
    }
}

export default LoadJobs;
