import React from 'react';
import Layout from '../Layout';
import { Link } from 'react-router-dom';
import { userInfo } from '../../utils/auth';

const Dashboard = () => {
    const { email, role } = userInfo();
    const UserLinks = () => {
        return (
            <div className="card">
                <h4 className="card-header">User Links</h4>
                <ul className="list-group">
                    <li className="list-group-item">
                        {
                            role === 'worker'
                            &&
                            (<>
                                <Link className="nav-link" to="#">My Deals</Link>
                                <Link className="nav-link" to="#">My Applications</Link>
                                <Link className="nav-link" to="#">Find a job now</Link>
                            </>)
                        }
                        {
                            role === 'client'
                            &&
                            (
                                <>
                                    <Link className="nav-link" to="#">My Deals</Link>
                                    <Link className="nav-link" to="#">Preview Applications</Link>
                                    <Link className="nav-link" to="#">Post a new Job</Link>
                                </>
                            )
                        }

                    </li>
                    <li className="list-group-item">
                        <Link className="nav-link" to="#">Update Profile</Link>
                    </li>
                </ul>
            </div>
        )
    };

    const UserInfo = () => (
        <div className="card mb-5">
            <h3 className="card-header">User Information</h3>
            <ul className="list-group">
                <li className="list-group-item">{email}</li>
                <li className="list-group-item">{role}</li>
            </ul>
        </div>
    );

    return (
        <Layout title="Dashboard" className="container-fluid">
            <div className="row">
                <div className="col-sm-3">
                    <UserLinks />
                </div>
                <div className="col-sm-9">
                    <UserInfo />
                </div>
            </div>
        </Layout>
    )
}

export default Dashboard;
