import { useState } from 'react';

function ThreeDotMenu({ onEditClick, onDeleteClick }) {
    const [isOpen, setIsOpen] = useState(false);

    function toggleMenu() {
        setIsOpen(!isOpen);
    }

    function handleEditClick() {
        onEditClick();
        setIsOpen(false);
    }

    function handleDeleteClick() {
        onDeleteClick();
        setIsOpen(false);
    }

    return (
        <div className="relative">
            <button onClick={toggleMenu} className="text-gray-600 hover:text-gray-800 focus:outline-none focus:text-gray-800">
                <svg className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M10 6a2 2 0 11-4 0 2 2 0 014 0zm0 8a2 2 0 11-4 0 2 2 0 014 0zm0-4a2 2 0 11-4 0 2 2 0 014 0z" clipRule="evenodd" />
                </svg>
            </button>

            {isOpen && (
                <div className="absolute z-10 right-0 mt-2 w-48 bg-white rounded-md shadow-lg">
                    <div className="py-1">
                        <button onClick={handleEditClick} className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900">
                            Edit
                        </button>
                        <button onClick={handleDeleteClick} className="block w-full text-left px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 hover:text-gray-900">
                            Delete
                        </button>
                    </div>
                </div>
            )}
        </div>
    );
}

export default ThreeDotMenu;
