'use strict';

var document = global.document

module.exports = function(o, range) {
	var start = range.start
	var end = range.end

	if (o.setSelectionRange == 'function'){
		o.setSelectionRange(start, end )
	} else {
    	o.selectionStart = start
    	o.selectionEnd = end
    }
}
